import React, { useEffect, useState } from 'react';
import API from '../utils/api';
import NestEggChart from '../components/NestEggChart';

function Dashboard() {
  const [userData, setUserData] = useState({
    name: "",
    scenarioName: "My Scenario",
    currentYear: new Date().getFullYear(),
    currentAge: 30,
    retirementAge: 65,
    currentSalary: 60000,
    annualContribution: 15,
    salaryGrowthRate: 2,
    currentNestEgg: 50000,
    rateOfReturnBeforeRetirement: 7,
    spendingAtRetirement: 48000,
    slowdownAge: 80,
    spendingAtSlowdown: 36000,
    rateOfReturnInRetirement: 4,
    inflationInRetirement: 2,
    married: false,
    socialSecurity: false,
  });
  
  const [projectionData, setProjectionData] = useState(null);
  const [savedScenarios, setSavedScenarios] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch user data and saved scenarios
    Promise.all([
      API.get('/user/me'),
      API.get('/scenarios')
    ]).then(([userRes, scenariosRes]) => {
      setUserData(prev => ({ ...prev, name: userRes.data.name }));
      setSavedScenarios(scenariosRes.data);
    }).catch(err => setError("Failed to load data"));
  }, []);

  const calculateProjections = () => {
    let years = [];
    let nestEgg = userData.currentNestEgg;
    let salary = userData.currentSalary;
    const retirementYear = userData.currentYear + (userData.retirementAge - userData.currentAge);
    const returnRateBeforeRetirement = userData.rateOfReturnBeforeRetirement / 100;
    const returnRateInRetirement = userData.rateOfReturnInRetirement / 100;
    const salaryGrowth = userData.salaryGrowthRate / 100;
    const inflation = userData.inflationInRetirement / 100;
    
    let maxNestEgg = nestEgg;
    let totalRetirementDollars = 0;

    for (let year = userData.currentYear; year <= userData.currentYear + 100; year++) {
      if (year < retirementYear) {
        // Accumulation phase
        const contribution = salary * (userData.annualContribution / 100);
        nestEgg = (nestEgg + contribution) * (1 + returnRateBeforeRetirement);
        salary *= (1 + salaryGrowth);
      } else {
        // Withdrawal phase
        const baseWithdrawal = year < userData.slowdownAge 
          ? userData.spendingAtRetirement 
          : userData.spendingAtSlowdown;
        const withdrawal = baseWithdrawal * Math.pow(1 + inflation, year - retirementYear);
        
        nestEgg = (nestEgg - withdrawal) * (1 + returnRateInRetirement);
        totalRetirementDollars += withdrawal;
      }

      maxNestEgg = Math.max(maxNestEgg, nestEgg);
      years.push({ year, balance: Math.max(0, nestEgg) });
      
      if (nestEgg <= 0) break;
    }

    setProjectionData({ years, maxNestEgg, totalRetirementDollars });
  };

  const saveScenario = async () => {
    try {
      const scenarioData = {
        ...userData,
        projection_data: JSON.stringify(projectionData)
      };
      
      await API.post('/scenarios', scenarioData);
      const response = await API.get('/scenarios');
      setSavedScenarios(response.data);
    } catch (err) {
      setError("Failed to save scenario");
    }
  };

  const deleteScenario = (index) => {
    setSavedScenarios(savedScenarios.filter((_, i) => i !== index));
  };

  const handleChange = (e) => {
    const { name, value, type } = e.target;
    setUserData(prev => ({
      ...prev,
      [name]: type === "number" ? parseFloat(value) || 0 : value
    }));
  };

  return (
    <div className="p-6 max-w-7xl mx-auto grid grid-cols-1 lg:grid-cols-2 gap-6">
      {/* Calculator Form */}
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-2xl font-bold mb-6">Retirement Calculator</h2>
        
        <div className="grid grid-cols-2 gap-4">
          {Object.entries(userData).map(([key, value]) => 
            key !== 'name' && (
              <div key={key} className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  {key.replace(/([A-Z])/g, ' $1').toLowerCase()}
                </label>
                <input
                  type="number"
                  name={key}
                  value={value}
                  onChange={handleChange}
                  className="w-full p-2 border rounded focus:ring-2 focus:ring-blue-500"
                  step={key.includes('Rate') || key.includes('contribution') ? '0.1' : '1'}
                />
              </div>
            )
          )}
        </div>

        <div className="mt-6 space-y-4">
          <button
            onClick={calculateProjections}
            className="w-full bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 transition duration-200"
          >
            Calculate Projections
          </button>
          
          {projectionData && (
            <button
              onClick={saveScenario}
              className="w-full bg-green-600 text-white py-2 px-4 rounded hover:bg-green-700 transition duration-200"
            >
              Save Scenario
            </button>
          )}
        </div>
      </div>

      {/* Results and Chart */}
      <div className="bg-white p-6 rounded-lg shadow-md">
        <h2 className="text-2xl font-bold mb-6">Retirement Projections</h2>
        
        {projectionData && (
          <>
            <div className="grid grid-cols-2 gap-4 mb-6">
              <div className="p-4 bg-blue-50 rounded-lg">
                <p className="text-sm text-blue-600">Final Balance</p>
                <p className="text-2xl font-bold text-blue-900">
                  ${projectionData.years.slice(-1)[0].balance.toLocaleString()}
                </p>
              </div>
              
              <div className="p-4 bg-green-50 rounded-lg">
                <p className="text-sm text-green-600">Max Savings</p>
                <p className="text-2xl font-bold text-green-900">
                  ${projectionData.maxNestEgg.toLocaleString()}
                </p>
              </div>
            </div>

            <NestEggChart dataPoints={projectionData.years} />
          </>
        )}

        {error && (
          <div className="mt-4 p-4 bg-red-50 text-red-600 rounded-lg">
            {error}
          </div>
        )}
      </div>

      {/* Saved Scenarios */}
      {savedScenarios.length > 0 && (
        <div className="col-span-1 lg:col-span-2 bg-white p-6 rounded-lg shadow-md">
          <h2 className="text-2xl font-bold mb-6">Saved Scenarios Comparison</h2>
          <NestEggChart 
            dataPoints={savedScenarios.map(s => JSON.parse(s.projection_data).years)}
            multipleScenarios={true}
          />
        </div>
      )}
    </div>
  );
}

export default Dashboard;
