import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import API from '../utils/api';

function Navbar() {
  const navigate = useNavigate();
  const token = sessionStorage.getItem('token');
  const [userName, setUserName] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  useEffect(() => {
    if (token) {
      API.get('/user/me')
        .then(res => setUserName(res.data.name))
        .catch(err => console.error('Failed to fetch user data', err));
    }
  }, [token]);

  const handleLogout = () => {
    sessionStorage.removeItem('token');
    navigate('/login');
  };

  return (
    <nav className="bg-gradient-to-r from-blue-500 to-purple-500 p-4 text-white">
      <div className="max-w-7xl mx-auto flex flex-wrap justify-between items-center">
        {/* Logo and Brand Name */}
        <div className="flex items-center space-x-2">
          <img src="/Logo_Regular.svg" alt="RetireNow" className="h-12 w-12" />
          <Link to="/dashboard" className="text-2xl font-extrabold tracking-widest hover:text-gray-300 transition duration-300">
            RetireNow
          </Link>
        </div>

        {/* Hamburger Menu for Mobile */}
        <button 
          className="md:hidden p-2"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d={isMenuOpen ? "M6 18L18 6M6 6l12 12" : "M4 6h16M4 12h16M4 18h16"} />
          </svg>
        </button>

        {/* Navigation Links and User Info */}
        <div className={`${isMenuOpen ? 'block' : 'hidden'} md:flex w-full md:w-auto md:items-center mt-4 md:mt-0`}>
          {token ? (
            <div className="flex flex-col md:flex-row md:items-center space-y-4 md:space-y-0 md:space-x-6">
              <Link 
                to="/dashboard" 
                className="bg-blue-600 px-4 py-2 rounded hover:bg-blue-700 transition duration-300 text-center"
              >
                Dashboard
              </Link>
              <Link 
                to="/retirement-comparison" 
                className="bg-purple-600 px-4 py-2 rounded hover:bg-purple-700 transition duration-300 text-center"
              >
                Retirement Comparison
              </Link>
              <span className="text-lg text-center md:text-left">{userName}</span>
              <button
                onClick={handleLogout}
                className="bg-red-500 px-4 py-2 rounded hover:bg-red-600 transition duration-300"
              >
                Logout
              </button>
            </div>
          ) : (
            <Link
              to="/login"
              className="bg-green-500 px-4 py-2 rounded hover:bg-green-600 transition duration-300 block text-center"
            >
              Login
            </Link>
          )}
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
