import React, { useEffect, useState } from 'react';
import API from '../utils/api';
import NestEggChart from '../components/NestEggChart';

function Dashboard() {
  const [userData, setUserData] = useState({
    name: "",
    scenarioName: "",
    currentYear: 2022,
    currentAge: 30,
    retirementAge: 70,
    currentSalary: 60000,
    annualContribution: 10,
    salaryGrowthRate: 2,
    currentNestEgg: 0,
    rateOfReturnBeforeRetirement: 4.5,
    spendingAtRetirement: 44000,
    slowdownAge: 80,
    spendingAtSlowdown: 44000,
    rateOfReturnInRetirement: 4.0,
    inflationInRetirement: 1.5,
    married: false,
    socialSecurity: false,
  });
  
  const [projectionData, setProjectionData] = useState(null);
  const [savedScenarios, setSavedScenarios] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    API.get('/user/me')
      .then(res => setUserData(prev => ({ ...prev, name: res.data.name })))
      .catch(err => setError("Failed to load user data"));
  }, []);

  const calculateProjections = () => {
    let years = [];
    let nestEgg = userData.currentNestEgg;
    let salary = userData.currentSalary;
    let retirementYear = userData.currentYear + (userData.retirementAge - userData.currentAge);
    let returnRateBeforeRetirement = userData.rateOfReturnBeforeRetirement / 100;
    let returnRateInRetirement = userData.rateOfReturnInRetirement / 100;
    let salaryGrowth = userData.salaryGrowthRate / 100;
    let inflation = userData.inflationInRetirement / 100;
    let yearlyWithdrawals;
    let maxNestEgg = 0;
    let totalRetirementDollars = 0;

    for (let year = userData.currentYear; year <= 100 + userData.currentYear; year++) {
      if (year < retirementYear) {
        nestEgg += salary * (userData.annualContribution / 100);
        nestEgg *= (1 + returnRateBeforeRetirement);
        salary *= (1 + salaryGrowth);
      } else {
        yearlyWithdrawals = year < userData.slowdownAge ? userData.spendingAtRetirement : userData.spendingAtSlowdown;
        yearlyWithdrawals *= (1 + inflation) ** (year - retirementYear);
        nestEgg -= yearlyWithdrawals;
        totalRetirementDollars += yearlyWithdrawals;
        nestEgg *= (1 + returnRateInRetirement);
      }
      
      maxNestEgg = Math.max(maxNestEgg, nestEgg);
      years.push({ year, balance: nestEgg });
      if (nestEgg <= 0) break;
    }

    setProjectionData({ years, maxNestEgg, totalRetirementDollars });
  };

  const saveScenario = () => {
    setSavedScenarios([...savedScenarios, { ...userData, projectionData }]);
  };

  const deleteScenario = (index) => {
    setSavedScenarios(savedScenarios.filter((_, i) => i !== index));
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    setUserData(prev => ({ 
      ...prev, 
      [name]: type === "checkbox" ? checked : parseFloat(value) || 0
    }));
  };

  return (
    <div className="p-6 max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-6">
      <div className="bg-white p-6 rounded shadow-md">
        <h2 className="text-xl font-bold mb-4">Retirement Calculator</h2>
        
        {Object.keys(userData).map((key) => (
          key !== 'name' && (
            <div key={key} className="mb-4">
              <label className="block mb-2 capitalize">{key.replace(/([A-Z])/g, ' $1')}</label>
              <input 
                type={typeof userData[key] === "boolean" ? "checkbox" : "number"} 
                name={key} 
                checked={typeof userData[key] === "boolean" ? userData[key] : undefined}
                value={typeof userData[key] !== "boolean" ? userData[key] : undefined}
                onChange={handleChange} 
                className="w-full p-2 border rounded" 
              />
            </div>
          )
        ))}

        <button 
          onClick={calculateProjections} 
          className="w-full bg-blue-500 text-white p-2 rounded mt-4">
          Calculate
        </button>
        <button 
          onClick={saveScenario} 
          className="w-full bg-green-500 text-white p-2 rounded mt-4">
          Save Scenario
        </button>
      </div>

      <div className="bg-white p-6 rounded shadow-md">
        <h2 className="text-xl font-bold mb-4">Retirement Projections</h2>
        {savedScenarios.length > 0 && (
          <div className="mb-4">
            <h3 className="text-lg font-bold">Comparison Chart</h3>
            <NestEggChart dataPoints={savedScenarios.map(scenario => scenario.projectionData.years)} />
          </div>
        )}
        
        {projectionData && (
          <div>
            <p className="text-3xl font-bold text-blue-600 mb-4">${projectionData.years?.slice(-1)[0]?.balance.toFixed(2) || 0}</p>
            <p className="text-green-500">{projectionData.years?.slice(-1)[0]?.balance > 0 ? "You're on track" : "Your savings may run out"}</p>
            <p className="text-gray-600">Max Savings: <span className="font-bold">${projectionData.maxNestEgg.toFixed(2) || 0}</span></p>
            <p className="text-gray-600">Total Retirement Dollars Received: <span className="font-bold">${projectionData.totalRetirementDollars.toFixed(2) || 0}</span></p>
          </div>
        )}
      </div>

      <div className="bg-white p-6 rounded shadow-md col-span-2">
        <h2 className="text-xl font-bold mb-4">Saved Scenarios</h2>
        {savedScenarios.length > 0 ? (
          <ul>
            {savedScenarios.map((scenario, index) => (
              <li key={index} className="mb-2 p-2 border rounded flex justify-between">
                <span><strong>{scenario.scenarioName}</strong>: Max Savings ${scenario.projectionData.maxNestEgg.toFixed(2)}</span>
                <button onClick={() => deleteScenario(index)} className="bg-red-500 text-white px-2 py-1 rounded">Delete</button>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-gray-600">No saved scenarios yet.</p>
        )}
      </div>
    </div>
  );
}

export default Dashboard;
