import React, { useEffect, useState } from 'react';
import API from '../utils/api';
import NestEggChart from '../components/NestEggChart';

function Dashboard() {
  const [userData, setUserData] = useState({
    age: 24,
    retirementAge: 68,
    annualIncome: 70000,
    incomeIncrease: 5,
    currentSavings: 0,
    annualSavings: 10,
    annualSpending: 40000,
  });
  const [projectionData, setProjectionData] = useState(null);
  const [error, setError] = useState(null);
  
  useEffect(() => {
    API.get('/user/me')
      .then(res => setUserData(prev => ({ ...prev, name: res.data.name })))
      .catch(err => setError("Failed to load user data"));

    API.get('/retirement/projection')
      .then(res => setProjectionData(res.data))
      .catch(err => setError("Failed to load projection data"));
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setUserData(prev => ({ ...prev, [name]: value }));
  };

  return (
    <div className="p-6 max-w-6xl mx-auto grid grid-cols-1 md:grid-cols-2 gap-6">
      <div className="bg-white p-6 rounded shadow-md">
        <h2 className="text-xl font-bold mb-4">Retirement Calculator</h2>
        
        {Object.keys(userData).map((key) => (
          key !== 'name' && (
            <div key={key} className="mb-4">
              <label className="block mb-2 capitalize">{key.replace(/([A-Z])/g, ' $1')}</label>
              <input 
                type="number" 
                name={key} 
                value={userData[key]} 
                onChange={handleChange} 
                className="w-full p-2 border rounded" 
              />
            </div>
          )
        ))}
      </div>

      <div className="bg-white p-6 rounded shadow-md">
        <h2 className="text-xl font-bold mb-4">Your total savings at retirement</h2>
        <p className="text-3xl font-bold text-blue-600 mb-4">$3,874,958.26</p>
        <p className="text-green-500">You're on track</p>
        <p className="text-gray-600">Savings needed for retirement: <span className="font-bold">$653,183.43</span></p>
        <p className="text-gray-600">Extra savings: <span className="font-bold text-green-500">$3,221,774.83</span></p>
        
        <h2 className="text-lg font-bold mt-6">Your retirement summary</h2>
        <p className="text-gray-600">Your plan provides $3,874,958.26 when you retire. This assumes annual retirement expenses of $40,000.00.</p>
        
        <div className="mt-6">
          {projectionData ? (
            <NestEggChart dataPoints={projectionData.years} />
          ) : (
            <p className="text-gray-600">Loading projection data...</p>
          )}
        </div>
      </div>
    </div>
  );
}

export default Dashboard;
