import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function NestEggChart({ dataPoints, multipleScenarios = false }) {
  if (!dataPoints || dataPoints.length === 0) {
    return <p className="text-gray-600">No data available for the chart.</p>;
  }

  const colors = [
    'rgba(75,192,192,1)',
    'rgba(255,99,132,1)',
    'rgba(54, 162, 235, 1)',
    'rgba(255, 206, 86, 1)',
  ];

  const chartData = {
    labels: multipleScenarios ? dataPoints[0].map(pt => pt.year) : dataPoints.map(pt => pt.year),
    datasets: multipleScenarios 
      ? dataPoints.map((scenario, index) => ({
          label: `Scenario ${index + 1}`,
          data: scenario.map(pt => pt.balance),
          fill: false,
          borderColor: colors[index % colors.length],
          backgroundColor: colors[index % colors.length].replace('1)', '0.2)'),
          tension: 0.1,
        }))
      : [{
          label: 'Nest Egg Value',
          data: dataPoints.map(pt => pt.balance),
          fill: false,
          borderColor: colors[0],
          backgroundColor: colors[0].replace('1)', '0.2)'),
          tension: 0.1,
        }]
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        title: {
          display: true,
          text: 'Year'
        }
      },
      y: {
        title: {
          display: true,
          text: 'Balance ($)'
        },
        beginAtZero: true,
        ticks: {
          callback: value => `$${value.toLocaleString()}`
        }
      }
    },
    plugins: {
      tooltip: {
        callbacks: {
          label: context => `$${context.parsed.y.toLocaleString()}`
        }
      }
    }
  };

  return (
    <div className="h-64 md:h-96">
      <Line data={chartData} options={options} />
    </div>
  );
}

export default NestEggChart;
