import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function NestEggChart({ dataPoints }) {
  if (!dataPoints || dataPoints.length === 0) {
    return <p className="text-gray-600">No data available for the chart.</p>;
  }

  const chartData = {
    labels: dataPoints.map(pt => pt.year),
    datasets: [
      {
        label: 'Nest Egg Value',
        data: dataPoints.map(pt => pt.balance),
        fill: false,
        borderColor: 'rgba(75,192,192,1)',
        backgroundColor: 'rgba(75,192,192,0.2)',
        tension: 0.1,
      }
    ]
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    scales: {
      x: {
        title: {
          display: true,
          text: 'Year'
        }
      },
      y: {
        title: {
          display: true,
          text: 'Balance ($)'
        },
        beginAtZero: true
      }
    }
  };

  return (
    <div className="h-64 md:h-80">
      <Line data={chartData} options={options} />
    </div>
  );
}

export default NestEggChart;
