import React, { useEffect, useState } from 'react';
import API from '../utils/api';
import NestEggChart from '../components/NestEggChart';

function Dashboard() {
  const [projectionData, setProjectionData] = useState(null);
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Fetch user profile data
    API.get('/user/me')
      .then(res => setUserData(res.data))
      .catch(err => setError("Failed to load user data"));

    // Fetch retirement projection data
    API.get('/retirement/projection')
      .then(res => setProjectionData(res.data))
      .catch(err => setError("Failed to load projection data"));
  }, []);

  return (
    <div className="p-6 max-w-4xl mx-auto">
      {error && <p className="text-red-500 text-sm">{error}</p>}
      
      {userData && (
        <div className="bg-white p-6 rounded shadow-md mb-6">
          <h2 className="text-2xl font-bold mb-2">Welcome, {userData.name}!</h2>
          <p className="text-gray-600">Your estimated retirement savings are shown below.</p>
        </div>
      )}
      
      <div className="bg-white p-6 rounded shadow-md">
        <h2 className="text-xl font-bold mb-4">Retirement Projection</h2>
        {projectionData ? (
          <NestEggChart dataPoints={projectionData.years} />
        ) : (
          <p className="text-gray-600">Loading projection data...</p>
        )}
      </div>
    </div>
  );
}
export default Dashboard;
