import React, { useState } from 'react';
import API from '../utils/api';

function MonteCarlo() {
  const [userData] = useState({
    scenarioName: "Monte Carlo Simulation",
    currentYear: new Date().getFullYear(),
    currentAge: 30,
    retirementAge: 70,
    currentSalary: 60000,
    annualContribution: 10,
    salaryGrowthRate: 2,
    currentNestEgg: 10000, // updated from 0
    rateOfReturnBeforeRetirement: 4.5,
    spendingAtRetirement: 35000, // adjusted for sustainability
    slowdownAge: 80,
    spendingAtSlowdown: 30000,   // adjusted for sustainability
    rateOfReturnInRetirement: 4,
    inflationInRetirement: 1.5,
  });
  const [simResult, setSimResult] = useState(null);
  const [error, setError] = useState(null);

  const runMonteCarlo = async () => {
    try {
      const response = await API.post('/montecarlo', userData);
      console.log("Monte Carlo simulation response:", response.data);
      setSimResult(response.data);
      setError(null);
    } catch (err) {
      console.error('Failed to run simulation:', err);
      setError('Failed to run simulation');
      setSimResult(null);
    }
  };

  return (
    <div className="p-6 max-w-7xl mx-auto">
      <h2 className="text-2xl font-bold mb-4">Monte Carlo Simulation</h2>
      <button 
        onClick={runMonteCarlo}
        className="mb-4 bg-blue-600 text-white py-2 px-4 rounded hover:bg-blue-700 transition duration-200"
      >
        Run Simulation
      </button>
      {simResult ? (
        <div className="mt-4 p-4 bg-green-50 rounded">
          <p><strong>Iterations:</strong> {simResult.iterations}</p>
          <p>
            <strong>Average Final Balance:</strong> $
            {parseFloat(simResult.average_final_balance).toFixed(2)}
          </p>
          <pre className="mt-4 bg-gray-100 p-2 rounded overflow-auto max-h-60">
            {JSON.stringify(simResult.all_results, null, 2)}
          </pre>
        </div>
      ) : error ? (
        <div className="mt-4 p-4 bg-red-50 text-red-600 rounded">
          {error}
        </div>
      ) : (
        <p>No simulation run yet. Click "Run Simulation" to begin.</p>
      )}
    </div>
  );
}

export default MonteCarlo;
